help_define('_HELP_IJAVASUN0',              "=Java=\n\ns. [[Systemanforderungen]]\ns. http://java.sun.com/");
help_define('_HELP_IKDE0',                  "=KDE=\n\ns. [[Systemanforderungen]]\ns. http://www.kde.org/");
help_define('_HELP_IKONQUEROR0',            "=Konqueror=\n\ns. [[Systemanforderungen]]\ns. http://www.konqueror.org/");
help_define('_HELP_ILANGUAGES0',            "=Sprachen=\n\ns. [[iloginlogout|An- und Abmelden]]");
help_define('_HELP_ILINKS0',                "=Verweise=\n\ns. [[ieditattrselection|Attribute]]\ns. [[ireferencesofrecords|Referenzen]].");
